import axios from 'axios';

const API_URL = 'https://api.example.com/shorten'; // Replace with your actual API endpoint

export const shortenUrl = async (longUrl) => {
    try {
        const response = await axios.post(API_URL, { url: longUrl });
        return response.data.shortenedUrl; // Adjust based on your API response structure
    } catch (error) {
        console.error('Error shortening the URL:', error);
        throw error;
    }
};
