import axios from 'axios';

// const API_URL = 'https://api.example.com/shorten'; // Replace with your actual API endpoint

// export const shortenUrl = async (longUrl) => {
//     try {
//         const response = await axios.post(API_URL, { url: longUrl });
//         return response.data.shortenedUrl; // Adjust based on your API response structure
//     } catch (error) {
//         console.error('Error shortening the URL:', error);
//         throw error;
//     }
// };

// simple mock shorten function — replace with real API call if you have one
export const shortenUrl = async(url) => {
    // simulate network delay
    await new Promise((r) => setTimeout(r, 300));
    const id = Date.now().toString(36) + Math.random().toString(36).slice(2, 6);
    const host = window.location.origin || 'http://localhost:3000';
    const shortPath = `/${id}`;
    return {
        id,
        url,
        shortUrl: `${host}${shortPath}`,
    };
};
